mapper.insert:
insert into endorse (eID, accountID, blogID
  )
values (#{eID,jdbcType=INTEGER}, #{accountID,jdbcType=INTEGER}, #{blogID,jdbcType=INTEGER}
  )

mapper.update:
update endorse
set accountID = #{accountID,jdbcType=INTEGER},
  blogID = #{blogID,jdbcType=INTEGER}
where eID = #{eID,jdbcType=INTEGER}

mapper.select:
select eID, accountID, blogID
from endorse
where eID = #{eID,jdbcType=INTEGER}
