mapper.insert:
insert into followlist (fID, followID, fanID
  )
values (#{fID,jdbcType=INTEGER}, #{followID,jdbcType=INTEGER}, #{fanID,jdbcType=INTEGER}
  )

mapper.update:
update followlist
set followID = #{followID,jdbcType=INTEGER},
  fanID = #{fanID,jdbcType=INTEGER}
where fID = #{fID,jdbcType=INTEGER}

mapper.select:
select fID, followID, fanID
from followlist
where fID = #{fID,jdbcType=INTEGER}
